public class Evetntill100 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int num = 0; num <= 100; num++) {
			if (num % 2 == 0)
				System.out.println(num + " is Even");
			num++;

		}

	}

}
